definition.script:
node{
    try{
        stage('checkout') {
        git 'https://github.com/Pradeepa-geek/jenkins2-course-spring-boot.git'
        } 
        def projectPath="spring-boot-samples/spring-boot-sample-atmosphere/";
        dir(projectPath) {
           stage('clean compile package'){
               sh 'mvn clean package'
           }
           stage('Archive Artifacts'){
               archiveArtifacts artifacts: 'target/*.jar', followSymlinks: false
           }
      } 
         notify('Finished')
    }catch(error){
        notify('Error :'+error)
    }

}

def notify(status){
emailext body: status+':view Resutls here : <a href=$BUILD_URL>$JOB_NAME [$BUILD_NUMBER]</a>', subject: 'Finished Executing : Job $JOB_NAME', to: 'pradi.csp@gmail.com'
}
